    int countCtg(String str){
        int pos=str.indexOf("CTG");
        int count=0;
        if(pos==-1)
        return 0;
        else{
            while(pos!=-1){
            count++;
            pos=str.indexOf("CTG",pos+3);
        }
            
        
        }
        return count;
    
    }
    public double calCG(String DNA){
        int len=DNA.length();
        int count=0;
        for(int i=0;i<len;i++){
            if(DNA.charAt(i)=='C'||DNA.charAt(i)=='G')
            count++;
        }
        return ((double)count)/len;
        //System.out.println("cg"+count+ "len"+len+" ratio"+((float)count)/len);
    
    }
    int findStopCodon(String str,int start,String stopStr){
        
		if (start == -1) {
			return -1;
		}
		int stop = str.indexOf(stopStr, start+3);
		if ((stop - start) % 3 == 0) {
			return stop;
		}
		else {
			return -1;
		}
    
    }
